package com.aspose.omr.cloud.sdk.android;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;

import com.aspose.omr.cloud.sdk.android.databinding.ActivityMainBinding;

import androidx.appcompat.app.AppCompatActivity;

import android.os.SystemClock;
import android.util.Base64;
import android.view.View;
import android.widget.ImageView;

import androidx.navigation.ui.AppBarConfiguration;

import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.TimeUnit;

import aspose.omr.cloud.sdk.api.ApiClient;
import aspose.omr.cloud.sdk.api.GenerateTemplate;
import aspose.omr.cloud.sdk.models.BubbleSize;
import aspose.omr.cloud.sdk.models.Color;
import aspose.omr.cloud.sdk.models.FileExtension;
import aspose.omr.cloud.sdk.models.FontStyle;
import aspose.omr.cloud.sdk.models.OMRResponse;
import aspose.omr.cloud.sdk.models.OmrGenerateTask;
import aspose.omr.cloud.sdk.models.Orientation;
import aspose.omr.cloud.sdk.models.PageSettings;
import aspose.omr.cloud.sdk.models.PaperSize;


public class MainActivity extends AppCompatActivity {

    private AppBarConfiguration appBarConfiguration;
    private ActivityMainBinding binding;
    private GenerateTemplate api;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        binding = ActivityMainBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());
        ApiClient apiClient= new ApiClient("a31bec5f-9464-44c1-9c2a-d164ac567fea","442deef8031e3b7356d94b8898041747");
        api = new GenerateTemplate(apiClient);
    }
    public void generateClick(View v){
        PageSettings settings = new PageSettings();
        settings.fontFamily("Segoe UI");
        settings.fontStyle(FontStyle.REGULAR);
        settings.fontSize(12);
        settings.bubbleColor(Color.BLACK);
        settings.paperSize(PaperSize.A4);
        settings.pageMarginLeft(210);
        settings.orientation(Orientation.VERTICAL);
        settings.bubbleSize(BubbleSize.NORMAL);
        settings.outputFormat(FileExtension.PNG);
        OmrGenerateTask task = new OmrGenerateTask();
        Map<String, String> images = new HashMap<>();
        images.put("logo1.jpg","");
        images.put("logo2.png","");
        task.setMarkupFile("");
        task.settings(settings);
        task.images(images);
        final String[] s = {""};
        final ImageView img= (ImageView) findViewById(R.id.image_view);
        new Thread(new Runnable() {
            public void run() {

                String templateId = api.postGenerateTemplate(task);

                OMRResponse generationResult = new OMRResponse();

                while (true) {
                    generationResult = api.getGenerateTemplate(templateId);

                    if (generationResult.getResponseStatusCode().getValue() == "Ok") {
                        break;
                    }

                    System.out.println("Wait, please! Your request is still being processed");
                    SystemClock.sleep(5000);

                }

                String resImg = generationResult.getResults().get(0).getData();
                s[0] = resImg;

            }
        }).start();
        while (true){
        if(s[0] != ""){

        byte[] decodedString = Base64.decode(s[0], Base64.DEFAULT);
        Bitmap decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
        img.setImageBitmap(decodedByte);
        break;
        }
        }

    }
}